import cors from 'cors';
import express, {
  json,
  NextFunction,
  Request as ExRequest,
  Response as ExResponse,
  urlencoded,
} from 'express';
import { RegisterRoutes } from '../dist/routes';
import * as swaggerUi from 'swagger-ui-express';

import { ValidateError } from 'tsoa';

const app = express();
const port = 5000;

// open CORS to the world right now. Lock this down for production use
app.use(cors());

app.use(
  urlencoded({
    extended: true,
  })
);

// serve up swagger in the public folder. This is autogenerated with tsoa
app.use(express.static('public'));
app.use(
  '/docs',
  swaggerUi.serve,
  swaggerUi.setup(undefined, {
    swaggerOptions: {
      url: '/swagger.json',
    },
  })
);

app.use(json());

// registers all the autogenerated tsoa routes. Make sure this happens after setting up express middleware
RegisterRoutes(app);

//a basic error handler that turns validation errors into 422s with more descriptive messages
app.use(function errorHandler(
  err: unknown,
  req: ExRequest,
  res: ExResponse,
  next: NextFunction
): ExResponse | void {
  if (err instanceof ValidateError) {
    console.warn(`Caught Validation Error for ${req.path}:`, err.fields);
    return res.status(422).json({
      message: 'Validation Failed',
      details: err?.fields,
    });
  }
  if (err instanceof Error) {
    return res.status(500).json({
      message: 'Internal Server Error',
    });
  }

  next();
});

app.listen(port, () => console.log(`Listening on http://localhost:${port}`));
